    //T.L.E 181/182 test case passed
    public static List<Integer> findSubstring3(String s, String[] words) {
        ArrayList<Integer> indices = new ArrayList<>();
        HashMap<String,Integer> wordFreq = new HashMap<>();
        for (String word : words) {wordFreq.put(word,wordFreq.getOrDefault(word,0)+1);}

        int left=0,interval=words[0].length(),right=left+interval;
        while (left<=s.length()-words.length*interval){
            String currWord = s.substring(left,right);
            if (wordFreq.containsKey(currWord)){
                String currLine = s.substring(left,left+ words.length*interval);
                HashMap<String,Integer> currLineWordMap = new HashMap<>();
                int lineLeft = 0;
                boolean ok = true;
                while (lineLeft<=currLine.length()-interval){
                    String currLineWord = currLine.substring(lineLeft,lineLeft+interval);
                    int wordLineOccurrence = currLineWordMap.getOrDefault(currLineWord,0);
                    currLineWordMap.put(currLineWord,wordLineOccurrence+1);
                    int wordFreqOccurrence = wordFreq.getOrDefault(currLineWord,0);
                    if (wordLineOccurrence+1>wordFreqOccurrence){
                        ok=false;break;
                    }

                    lineLeft+=interval;
                }

                if (ok) indices.add(left);
            }
            right++;
            left++;
        }
        return indices;
    }

    //time -O(n*m) m=line size each time a line is a possible candidate
    public static List<Integer> findSubstring(String s, String[] words) {
        ArrayList<Integer> indices = new ArrayList<>();
        HashMap<String,Integer> wordFreq = new HashMap<>();
        for (String word : words) {wordFreq.put(word,wordFreq.getOrDefault(word,0)+1);}

        int left=0,interval=words[0].length(),right=left+interval;
        while (left<=s.length()-words.length*interval){
            String currWord = s.substring(left,right);
            if (wordFreq.containsKey(currWord)){
                String currLine = s.substring(left,left+ words.length*interval);
                HashMap<String,Integer> currLineWordMap = new HashMap<>();
                int lineLeft = 0;
                while (lineLeft<=currLine.length()-interval){
                    String currLineWord = currLine.substring(lineLeft,lineLeft+interval);
                    currLineWordMap.put(currLineWord,currLineWordMap.getOrDefault(currLineWord,0)+1);
                    lineLeft+=interval;
                }

                boolean ok = currLineWordMap.size() == wordFreq.size();
                for (String word : wordFreq.keySet()) {
                    if (!currLineWordMap.containsKey(word)){
                        ok=false;break;
                    }
                    if ( currLineWordMap.get(word)<wordFreq.get(word)){
                        ok=false;break;
                    }
                }
                if (ok) indices.add(left);
            }
//                System.out.println(currWord);
            right++;
            left++;
        }
        return indices;
    }
